import './App.css';
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';

function App() {
  const [tasks, setTasks] = useState([
    { id: 1, name: 'Praproposal Skripsi', deadline: '15 Agustus 2024' },
    { id: 2, name: 'Proposal Skripsi', deadline: '31 Agustus 2024' },
  ]);

  const [newTask, setNewTask] = useState({ name: '', deadline: '' });

  const addTask = () => {
    if (newTask.name && newTask.deadline) {
      setTasks([...tasks, { id: tasks.length + 1, ...newTask }]);
      setNewTask({ name: '', deadline: '' });
    }
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <Router>
      <div className="app-container">
        <h1>To-Do List Tugas</h1>
        <div className="task-list">
          {tasks.map((task) => (
            <div key={task.id} className="task-item">
              <span>{task.name} - {task.deadline}</span>
              <button onClick={() => deleteTask(task.id)} className="delete-btn">Delete</button>
            </div>
          ))}
        </div>
        <div className="add-task-form">
          <input 
            type="text" 
            placeholder="Nama Tugas" 
            value={newTask.name} 
            onChange={(e) => setNewTask({ ...newTask, name: e.target.value })} 
          />
          <input 
            type="date" 
            placeholder="Deadline" 
            value={newTask.deadline} 
            onChange={(e) => setNewTask({ ...newTask, deadline: e.target.value })} 
          />
          <button onClick={addTask} className="add-btn">Add Task</button>
        </div>
      </div>
    </Router>
  );
}

export default App;
